import React ,{useState}from "react"
import List from './List';

function Todo() {
    const [task,setTask] = useState("")
    const [todos,setTodos] =useState([])
    const changeHandler = (e) =>{
        setTask(e.target.value);
    }
    const submitHandler = (e)=>{
        e.preventDefault()
        console.log(task)
        const newTodos = [...todos,task]
        setTodos(newTodos)
        setTask("");
    }
    const deleteHandler =(indexVal) =>{
        const newTodos = todos.filter((todo,index)=>index!=indexVal)
    }
  return (<div>
      <center>
          <div className="card">
              <div className="card-body">
                  <h5 className="card-title">ToDo Application</h5>
                  <form onSubmit={submitHandler}>
                      <input type="text" name="task" value={task} onChange={changeHandler}></input> &nbsp;
                      <input type="submit" value="add" name="add"></input>
                  </form>
                  <List todos={todos} deleteHandler={deleteHandler}></List>
              </div>
          </div>
      </center>
  </div>)
}

export default Todo
